sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	/**
	Turn-around calcuation:  
	Calculates when the state is not a pause state: 18=Awaiting Info 21=Awaiting Project Completion
	This condition exists because 'turn-around' indicates the case is beyond a pause state. 
	*/
	//if (current.state != 18 && current.state != 21 && current.state != 1 && !gs.nil(current.oca_ocr_received_date_time))) {
	// always query for pause durations if the received date is not null.  there's always a chance conditions created
	// an sla that at one time had pause duration.
	if (!gs.nil(current.oca_ocr_received_date_time)) {
		var start = new GlideDateTime();
		start.setDisplayValue(current.oca_ocr_received_date_time.getDisplayValue());
		var now = new GlideDateTime();
		if(!gs.nil(current.resolution_date_resolved)){
			now.setDisplayValue(current.resolution_date_resolved.getDisplayValue());
		}
		var dur = GlideDateTime.subtract(start, now);
		var pauseDur = new GlideDuration();
		pauseDur.setDisplayValue("00:00:00");
		var sla = new GlideRecord('task_sla');
		sla.addQuery('task', current.sys_id);
		sla.addQuery('sla.set_start_to', 'oca_ocr_received_date_time');
		//sla.addQuery('sla.pause_condition','state=18^EQ');
		//sla.addQuery('sla.pause_condition','state=18^awaiting_info_fromISNOTEMPTY^awaiting_info_from.external=true^NQstate=21^EQ');
		// below query indicates an sla definition with an actual pause time.  
		//sla.addNullQuery('sla.duration_type');
		sla.orderByDesc('end_time');
		var durations = [];
		sla.query();
		// technically, if the SLA is defined such that it starts with the OCA/ORA received date
		// and it pauses on awaiting info,
		// then any one SLA can be queried to calculate turn-around time.
		var isPaused=false;
		while(sla.next()){
			if(!gs.nil(sla.pause_duration)){
				var pauseDuration = new GlideDuration();
				pauseDuration.setValue(sla.pause_duration);
				durations.push(pauseDuration);
			}
			if (sla.stage=='paused') {
				isPaused=true;
			}
		}
		if (isPaused) {
			gs.addInfoMessage('The case has task sla records that are paused. Turn-around time calculation may be affected.');
		}
		var finalDur = dur;
		if (durations.length>0) {
			for (var i=0; i<durations.length; i++) {
				finalDur.subtract(durations[i]);
			}
		}
		current.setValue('turn_around_time',finalDur.getValue());
	} else {
		current.setValue('turn_around_time',0);
	}
	//current.update();
})(current, previous);
