sys_script_client.script:
function onLoad() {

	/*    
	// Echo All Sections
	var s = g_form.getSectionNames();

	var msg = ' | ';
	for (var i = 0; i < s.length; i++) {
		msg += s[i] + ' |  ';

	}

	g_form.addInfoMessage(msg);
 	*/



	if (g_form.getValue('contact_is_member') == 'true') {
		//g_form.addInfoMessage('hiding member tab, contact is member');
		g_form.setMandatory('member_ssn', false);
		g_form.setMandatory('member_alt_id', false);
		g_form.setSectionDisplay('memberprovider', false);
	} else if (g_form.getValue('contact_is_member') == 'false') {
		//g_form.addInfoMessage('showing member tab, member is member');
		g_form.setMandatory('member_ssn', true);
		g_form.setMandatory('member_alt_id', true);
		g_form.setSectionDisplay('memberprovider', true);
	}

	if (g_form.getValue('contact_is_patient') == 'true' || g_form.getValue('member_is_patient') == 'true') {
		//g_form.addInfoMessage('hiding patient tab, contact or member is patient');
		g_form.setSectionDisplay('patient', false);
	} else if (g_form.getValue('contact_is_patient') == 'false' && g_form.getValue('member_is_patient') == 'false') {
		//g_form.addInfoMessage('showing patient tab, patient is patient');
		g_form.setSectionDisplay('patient', true);
	}


}
