sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (isLoading || newValue === '') {

		return;

	} else {

		var ga = new GlideAjax('x_opt_ecm.ECMAjaxUtil');

		ga.addParam('sysparm_name','getResolutionOutcomes');

		ga.addParam('sysparm_subtype', newValue);

		ga.getXML(UpdateChoiceValues);


	}
}


//process response

function UpdateChoiceValues(response) {

	var select = g_form.getControl('sys_select.x_opt_ecm_case.resolution_outcome');
	var val = select.value;

	g_form.clearOptions('sys_select.x_opt_ecm_case.resolution_outcome');
	g_form.clearOptions('resolution_outcome');

	var arrOption = response.responseXML.documentElement.getAttribute('answer');
	arrOption = JSON.parse(arrOption); //Transform the JSON string to an object
	g_form.addOption('sys_select.x_opt_ecm_case.resolution_outcome', '', '-- None --');
	for(var i=0;i<arrOption.length;i++) {
		g_form.addOption('sys_select.x_opt_ecm_case.resolution_outcome', arrOption[i].id, arrOption[i].label);
		if (arrOption[i].id==val) {
			g_form.setValue('resolution_outcome',val);
		}
	}

}
